package com.example.kingsofkodeimport android.graphics.Colorimport android.media.MediaPlayerimport android.os.Bundleimport android.widget.Buttonimport android.widget.ImageButtonimport androidx.appcompat.app.AppCompatActivityclass BoardGameActivity : AppCompatActivity() {    private var listOfDiceState = BooleanArray(6)    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.board_layout)        // Die state management        val die1 = findViewById<ImageButton>(R.id.die1)        val die2 = findViewById<ImageButton>(R.id.die2)        val die3 = findViewById<ImageButton>(R.id.die3)        val die4 = findViewById<ImageButton>(R.id.die4)        val die5 = findViewById<ImageButton>(R.id.die5)        val die6 = findViewById<ImageButton>(R.id.die6)        die1.setOnClickListener{onDiceClick(0, die1)}        die2.setOnClickListener{onDiceClick(1, die2)}        die3.setOnClickListener{onDiceClick(2, die3)}        die4.setOnClickListener{onDiceClick(3, die4)}        die5.setOnClickListener{onDiceClick(4, die5)}        die6.setOnClickListener{onDiceClick(5, die6)}        val rollButton = findViewById<Button>(R.id.roll_button)        rollButton.setOnClickListener{            val mediaPlayer: MediaPlayer? = MediaPlayer.create(this, R.raw.dice_roll)            mediaPlayer?.start()            // TODO: Add function call to send roll information to backend here        }    }    private fun onDiceClick(dieNumber: Int, die: ImageButton) {        listOfDiceState[dieNumber] = !listOfDiceState[dieNumber]        if (listOfDiceState[dieNumber])            die.setBackgroundColor(Color.DKGRAY)        else            die.setBackgroundColor(Color.LTGRAY)    }}