package com.example.kingsofkode.GameOverimport android.os.Bundleimport android.widget.ImageViewimport android.widget.TextViewimport androidx.appcompat.app.AppCompatActivityimport com.example.kingsofkode.Rimport com.example.kingsofkode.utils.Companion.getDrawableFromStringclass GameOverActivity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.game_over)        val gameState = this.intent.getStringExtra("gameState")!!        // set victory/defeat message        val gameStateText = findViewById<TextView>(R.id.gameState)        if (gameState == "win") {            gameStateText.text = "Victory!"        }        else {            gameStateText.text = "Defeat..."        }        val firstPlayer = findViewById<ImageView>(R.id.firstPlayer)        val firstScore = findViewById<TextView>(R.id.firstScore)        val secondPlayer = findViewById<ImageView>(R.id.secondPlayer)        val secondScore = findViewById<TextView>(R.id.secondScore)        val thirdPlayer = findViewById<ImageView>(R.id.thirdPlayer)        val thirdScore = findViewById<TextView>(R.id.thirdScore)        val fourthPlayer = findViewById<ImageView>(R.id.fourthPlayer)        val fourthScore = findViewById<TextView>(R.id.fourthScore)        // get top scores        val scoreArray = this.intent.getIntegerArrayListExtra("scoreArray")!!        // set top scores        firstScore.text = scoreArray[0].toString()        secondScore.text = scoreArray[1].toString()        thirdScore.text = scoreArray[2].toString()        fourthScore.text = scoreArray[3].toString()        // get player rankings        val playerRankings = this.intent.getStringArrayListExtra("playerRankings")!!        firstPlayer.setImageDrawable(getDrawableFromString(this, playerRankings[0], resources))        secondPlayer.setImageDrawable(getDrawableFromString(this, playerRankings[1], resources))        thirdPlayer.setImageDrawable(getDrawableFromString(this, playerRankings[2], resources))        fourthPlayer.setImageDrawable(getDrawableFromString(this, playerRankings[3], resources))    }}