package com.example.kingsofkodeimport android.os.Bundleimport androidx.appcompat.app.AppCompatActivityimport androidx.viewpager2.widget.ViewPager2import java.util.*class MainActivity : AppCompatActivity() {    private val arrayList = ArrayList<String>()    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.character_picker_layout)        val myViewPager2 = findViewById<ViewPager2>(R.id.character_picker)        arrayList.add("go")        arrayList.add("java")        arrayList.add("php")        arrayList.add("python")        arrayList.add("rust")        val myAdapter = MyAdapter(this, arrayList)        myViewPager2.orientation = ViewPager2.ORIENTATION_HORIZONTAL        myViewPager2.adapter = myAdapter    }}