package com.example.kingsofkode.characterselectimport android.content.Intentimport android.media.MediaPlayerimport android.os.Bundleimport android.widget.Buttonimport androidx.appcompat.app.AppCompatActivityimport androidx.viewpager2.widget.ViewPager2import com.example.kingsofkode.Rimport com.example.kingsofkode.boardgame.BoardGameActivityimport java.util.*class MainActivity : AppCompatActivity() {    private val arrayList = arrayListOf("go", "java", "php", "python", "rust")    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.character_picker_layout)        val myViewPager2 = findViewById<ViewPager2>(R.id.character_picker)        val myAdapter = CharacterSelectAdapter(this, arrayList)        myViewPager2.orientation = ViewPager2.ORIENTATION_HORIZONTAL        myViewPager2.adapter = myAdapter        val startGameButton = findViewById<Button>(R.id.start_game_button)        startGameButton.setOnClickListener {            val mediaPlayer: MediaPlayer? = MediaPlayer.create(this, R.raw.game_start_fanfare)            mediaPlayer?.start()            val boardGameIntent = Intent(this@MainActivity, BoardGameActivity::class.java)            boardGameIntent.putExtra("player", arrayList[myViewPager2.currentItem % 5])            startActivity(boardGameIntent)        }        val helpButton = findViewById<Button>(R.id.help_button)        helpButton.setOnClickListener{            HelpFragment().show(supportFragmentManager, HelpFragment.TAG)        }    }}